questionvariables.text:
zahl: rand_with_step(1,3,1);
liste: [0,pi/2,pi];
phi: liste[zahl];

sel: rand([1,2]);

liste_at1:[pi/3,pi/6];
at1: liste_at1[sel];

liste_at2: [pi/6,pi/3];
at2: liste_at2[sel];

at3: pi/6;

c2: rand_with_step(3,9,1);
c1: rand_with_prohib(3,9,[c2]);

c3: (phi-at1*c1-at2*c2)/at3;
c3_abs: abs(c3);


liste_z1_re: [1,3];
z1_re: liste_z1_re[sel];
z1_im: sqrt(3);
phi1: atan(z1_im/z1_re);

liste_z2_re: [3,1];
z2_re: liste_z2_re[sel];
z2_im: sqrt(3);
phi2: atan(z2_im/z2_re);

z3_re: sqrt(3);
z3_im: 1;
phi3: atan(z3_im/z3_re);

z1: (z1_re)+(z1_im)*%i;
z2: (z2_re)+(z2_im)*%i;
z3: (z3_re)+(z3_im)*%i;

fi: rectform(cos(c1*phi1)+ %i*sin(c1*phi1))*(cos(c2*phi2)+ %i*sin(c2*phi2))*(cos(c3*phi3)+ %i*sin(c3*phi3));

loes: rectform(abs(z1)^c1*abs(z2)^c2*abs(z3)^c3*(cos(phi)+sin(phi)*%i));
