/* &#252;berpr&#252;fe, ob ans als Wahrscheinlichkeit interpretiert werden kann */
probabilityp(ans) := is(numberp(float(ans)) and ans >= 0 and ans <= 1)
/* runde x auf 3 Nachkommastellen */
dp(x) := decimalplaces(x, 3)
/* stelle x in wissenschaftlicher Notation dar */
SCI(x) := scientific_notation(x, 2)
/* Rundungsgenauigkeit bei numerischen Antworttests */
epsilon: 0.001

/* erzeuge zuf&#228;llige Verteilungen f&#252;r P */
x: matrix([rand(5), rand(5), rand(5)]) + 1
y: matrix([rand(5), rand(5), rand(5)]) + 1
z: matrix([rand(9), rand(9), rand(9)], [rand(9), rand(9), rand(9)], [rand(9), rand(9), rand(9)]) + 1
X: x / sum(x[1, j], j, 1, 3)
Y: y / sum(y[1, j], j, 1, 3)
Z: z / sum(sum(z[i, j], i, 1, 3), j, 1, 3)

/* Randomisiere zwischen Unabh&#228;ngigkeit und Abh&#228;ngigkeit */
/* Der Fall rand(2) = 0 ergibt immer Unabh&#228;ngigkeit. */
/* Der Fall rand(2) = 1 ergibt h&#246;chstwahrscheinlich Abh&#228;ngigkeit. */
if is(rand(2) = 0) then DistrP: transpose(X).Y else DistrP: Z

/* Randverteilung von X */
ProbX(k) := sum(DistrP[k, j], j, 1, 3)
DistrX: matrix([ProbX(1), ProbX(2), ProbX(3)])
/* Randverteilung von Y */
ProbY(l) := sum(DistrP[i, l], i, 1, 3)
DistrY: matrix([ProbY(1), ProbY(2), ProbY(3)])
/* Produktverteilung von X und Y */
ProbXY(k, l) := ProbX(k) * ProbY(l)
DistrXY: transpose(DistrX).DistrY

/* L&#246;sungen */
Tans3: [["unabh&#228;ngig", is(equal(DistrP, DistrXY))], ["nicht unabh&#228;ngig", is(notequal(DistrP, DistrXY))]]
